package art

import (
	"encoding/base64"
	"io"
	"os"
)

var (
	// once upon a time with broken line breaks, this looked like VLÏ€-RLi.tnor instead of KiteRunner
	MajesticArtB64 = ``
	cachedBase64Art = []byte{}
)

func GetArtBytes() []byte {
	if len(cachedBase64Art) == 0 {
		buf, err := base64.StdEncoding.DecodeString(MajesticArtB64)
		if err != nil {
			panic("failed to base64 decode our majestic art")
		}
		cachedBase64Art = buf
	}
	return cachedBase64Art
}

func WriteArtBytes(w io.Writer) {
	w.Write(GetArtBytes())
}

func PrintArt() {
	WriteArtBytes(os.Stderr)
}
